<template>
  <div class="page">
    <div class="all">
      <h1>发起筹款</h1>
      <el-form :model="oneDonate" status-icon :rules="rules" ref="oneDonate" label-width="100px" class="demo-ruleForm">
        <el-form-item label="筹款类型" prop="type">
          <el-radio-group v-model="oneDonate.type">
            <el-radio style="font-weight: bold;" label="多次取款型"></el-radio>
            <el-radio style="font-weight: bold;" label="单次取款型"></el-radio>
          </el-radio-group>
        </el-form-item>
        <el-form-item label="筹款时长" prop="time">
          <el-input v-model.number="oneDonate.time" style="width:450px; float:left"></el-input>
          <span>小时</span>
        </el-form-item>
        <el-form-item class="bor" label="收款地址" prop="addr">
          <el-input v-model="oneDonate.addr"></el-input>
        </el-form-item>
        <el-form-item class="bor" label="筹款目标" prop="amount">
          <el-input v-model.number="oneDonate.amount" style="width:450px; float:left"></el-input>
          <span>wei</span>
        </el-form-item>
        <el-form-item>
          <el-button type="primary" @click="setup('oneDonate')">发起筹款</el-button>
          <el-button @click="returnMain">返回主页</el-button>
        </el-form-item>
      </el-form>
    </div>
    <el-dialog
      :modal="false"
      title="成功发起筹款"
      :visible.sync="dialogVisible"
      width="50%">
      <span>项目地址：{{ conaddr }}</span>
      <span slot="footer" class="dialog-footer">
        <el-button type="primary" @click="succeed">返回主页</el-button>
      </span>
    </el-dialog>
  </div>
</template>

<style scoped>
  .page{
    position: fixed;
    background: url('~@/assets/aaa.jpeg') center center no-repeat;
    width: 100vw;
    height: 100vh;
  }

  .bor{
    border: 0px solid rgb(47, 0, 255);
  }

  .all{
    font-weight: bold;
    padding-top: 15px;
    padding-right: 30px;
    margin: auto;
    width: 600px;
    border: 0px solid red;
    background: white;
    opacity: 0.80;
    border-radius: 20px;
    position: absolute;
    left: 50%;
    top: 50%;
    -webkit-transform: translate(-50%, -50%);
    -moz-transform: translate(-50%, -50%);
    -ms-transform: translate(-50%, -50%);
    -o-transform: translate(-50%, -50%);
    transform: translate(-50%, -50%);
  }
</style>

<script>
import Fundraising from "../../../build/contracts/Fundraising.json";
import Fundraising2 from "../../../build/contracts/Fundraising2.json";
import Blacklist from "../../../build/contracts/Blacklist.json";

export default {
  data() {
    return {
      dialogVisible: false,
      conaddr: '',

      oneDonate: {
        type: '',
        time: '',
        addr: '',
        amount: '',
      },

      rules: {
        type: [
          { required: true, message: '请选择筹款类型', trigger: 'change' }
        ],
        
        time: [
          { required: true, message: '筹款时长不能为空', trigger: 'blur'  },
          { type: 'number', message: '筹款时长必须为数字值', trigger: 'blur'  }
        ],

        addr: [
          { required: true, message: '请输入你的地址', trigger: 'blur' },
          { min: 42, max: 42, message: '长度应为42个字符', trigger: 'blur' }
        ],

        amount: [
          { required: true, message: '筹款目标不能为空',  trigger: 'blur' },
          { type: 'number', message: '筹款目标必须为数字值', trigger: 'blur' }
        ]
      },
    }
  },
  methods: {
    succeed() {
      this.dialogVisible = false;
      this.$router.push({path:'/'});
    },

    returnMain() {
      this.$router.push({path:'/'});
    },

    setup(oneDonate) {
      this.$refs[oneDonate].validate((valid) => {
        if (valid) {
          var blacklist = new this.$web3.eth.Contract(Blacklist.abi, '0x8c5d2581Ef1c72BE24549908b5a146F9580101fe');
          blacklist.methods.bad(this.oneDonate.addr).call(null,(error,result)=>{
            if(result == 1){
              this.$message({
                message: '黑名单账户没有权利发起筹款',
                type: 'error'
              });
            }
            else{
              var tokenContract;
              if(this.oneDonate.type == '多次取款型'){
                tokenContract = new this.$web3.eth.Contract(Fundraising.abi, null, {
                  from: this.oneDonate.addr,
                });

                tokenContract.deploy({
                  data: '',
                  arguments: [this.oneDonate.addr, this.oneDonate.amount+'', this.oneDonate.time * 3600]
                }).send({
                  from: this.oneDonate.addr,
                  gas: 1500000,
                  gasPrice: '300000000000'
                }, (error, transactionHash)=>{
                  console.log("deploy tx hash:"+transactionHash)
                  if (error) {
                    this.$message({
                      message: '发起筹款失败，请检查账户状态',
                      type: 'error'
                    });
                  }
                })
                .on('error', function(error){ console.error(error) })
                .on('transactionHash', function(transactionHash){ console.log("hash:",transactionHash)})
                .on('receipt', function(receipt){
                  console.log(receipt.contractAddress) // contains the new contract address
                })
                .on('confirmation', function(confirmationNumber, receipt){console.log("receipt,",receipt)})
                //.then(function(newContractInstance){
                .then((newContractInstance)=>{
                  console.log(newContractInstance.options.address) // instance with the new contract address
                  this.conaddr = newContractInstance.options.address;
                  this.dialogVisible = true;
                });
              }
              else{
                console.log("单次取款");
                tokenContract = new this.$web3.eth.Contract(Fundraising2.abi, null, {
                  from: this.oneDonate.addr,
                });

                tokenContract.deploy({
                  data: '',
                  arguments: [this.oneDonate.addr, this.oneDonate.amount+'', this.oneDonate.time * 3600]
                }).send({
                  from: this.oneDonate.addr,
                  gas: 1500000,
                  gasPrice: '300000000000'
                }, (error, transactionHash)=>{
                  console.log("deploy tx hash:"+transactionHash)
                  if (error) {
                    this.$message({
                      message: '发起筹款失败，请检查账户状态',
                      type: 'error'
                    });
                  }
                })
                .on('error', function(error){ console.error(error) })
                .on('transactionHash', function(transactionHash){ console.log("hash:",transactionHash)})
                .on('receipt', function(receipt){
                  console.log(receipt.contractAddress) // contains the new contract address
                })
                .on('confirmation', function(confirmationNumber, receipt){console.log("receipt,",receipt)})
                //.then(function(newContractInstance){
                .then((newContractInstance)=>{
                  console.log(newContractInstance.options.address) // instance with the new contract address
                  this.conaddr = newContractInstance.options.address;
                  this.dialogVisible = true;
                });
              }
            }
          })
        } 
        else {
          console.log('error submit!!');
          this.$message({
            message: '请正确填写信息',
            type: 'error'
          });
          return false;
        }
      });
    },
  }
}
</script>